(function(){$(function(){$(".header").load("header.html",function(){$(".header .nav a").removeClass("active").eq(4).addClass("active")});$(".footer").load("footer.html");var b=0;$.ajax({url:"ajax/selectNews.php",data:{pagenum:0},success:a});$(".more").bind("click",function(){b++;$.ajax({url:"ajax/selectNews.php",data:{pagenum:b},success:a})})});function a(e){var c="";if(e.data.length<6){$(".more").remove();$(".mei").css("display","block")}for(var b=0;b<e.data.length;b++){c+='<li><div class="txt"><h2> <a href="newsdetail.html?id='+e.data[b].id+'">'+e.data[b].title+'</a> </h2> <p class="desc">'+e.data[b].desc+'</p><p class="date">发布时间：'+e.data[b].date+' </p></div><div class="pic"> <a href="newsdetail.html?id='+e.data[b].id+'"> <img src="ajax/upload/'+e.data[b].src+'" alt=""/> </a></div></li>'}$("ul.news").append(c)}})();